jQuery(window).on("load", function() {
    $('#preloader').fadeOut(500);
    $('#main-wrapper').addClass('show');

    $('body').attr('data-sidebar-style') === "mini" ? $(".hamburger").addClass('is-active') : $(".hamburger").removeClass('is-active')
});

(function($) {
    "use strict";

    $("#menu").metisMenu();

    $('.nk-nav-scroll').slimscroll({
        position: "right",
        size: "5px",
        height: "100%",
        color: "transparent"
    });


    $(".nav-control").on('click', function() {

        $('#main-wrapper').toggleClass("menu-toggle");
        $(".hamburger").toggleClass("is-active");
    });

    
    $(function() {
        for (var nk = window.location, 
            o = $("ul#menu a").filter(function() {
                    return this.href == nk;
                })
                .addClass("active")
                .parent()
                .addClass("active");;) {
            if (!o.is("li")) break;
            o = o.parent()
                .addClass("in")
                .parent()
                .addClass("active");
        }
    });

    $(function() {
        var win_h = window.outerHeight;
        if (win_h > 0 ? win_h : screen.height) {
            $(".content-body").css("min-height", (win_h + 60) + "px");
        };
    });

    
    $(function() {
        var win_h = window.innerHeight;
        if (win_h > 0 ? win_h : screen.height) {
        	
        	let result = $('.header-row').innerHeight() + $('.footer').innerHeight();
        	let mainheight = win_h - result;
            $(".content-main").css("min-height", mainheight  + "px");
           
            console.log("메인높이" + mainheight);
            console.log("아우터윈도우높이 " +win_h);
            console.log("result (헤더+푸더)" + result);
            console.log('아푸터헤더 높이' + $('.header-row').outerHeight());
             console.log('아우터푸터 높이' + $('.footer').outerHeight());
            console.log('이너헤더 높이' + $('.header-row').innerHeight());
            console.log('이너푸터 높이' + $('.footer').innerHeight());

        };
    });


    $('.selectpicker').selectpicker();


    $('[data-toggle="popover"]').popover();
    $('[data-toggle="tooltip"]').tooltip();



    const headerHight = $('.header').innerHeight();

    $(window).scroll(function() {
        if($('body').attr('data-layout') === "horizontal" && $('body').attr('data-header-position') === "static" && $('body').attr('data-sidebar-position') === "fixed")
            $(this.window).scrollTop() >= headerHight ? $('.metismenu').addClass('fixed') :  $('.metismenu').removeClass('fixed')
    });

    $('.sidebar-right-trigger').on('click', function() {
        $('.sidebar-right').toggleClass('show');
    });






})(jQuery);
