mapper.insert:
	INSERT INTO SMARTCALENDAR(TYPE, TITLE, CONTENT, STARTDATE, ENDDATE, ALLDAY, EMAIL, PROJECTIDX) 
	VALUES(#{type}, #{title}, #{content}, #{startDate}, #{endDate}, #{allDay}, #{email},

	     #{projectIdx} 
	     null 

  )

mapper.update:
UPDATE SMARTCALENDAR SET EMAIL=#{email}

											, TYPE=#{type}


											, TITLE=#{title}


											, CONTENT=#{content}


											, STARTDATE=#{startDate}


											, ENDDATE=#{endDate}


											, ALLDAY=	#{allDay}



												, PROJECTIDX =	#{projectIdx}


												, PROJECTIDX = null


WHERE CALIDX=#{calIdx}
