mapper.select:
SELECT BOARDIDX, EMAIL, TITLE, CONTENT, WRITEDATE 
	FROM owl.BOARD
WHERE PROJECTIDX = #{projectIdx}

SELECT BOARDIDX, EMAIL, TITLE, CONTENT, WRITEDATE 
	FROM owl.BOARD
WHERE BOARDIDX = #{boardIdx}

SELECT FILEIDX, BOARDIDX, WRITER, FILENAME, FILESIZE 
	FROM owl.FILE
WHERE BOARDIDX  = #{boardIdx}
